import React from 'react';

const UnrichList = () => {
    return (
        <div>
            <header>
                <h1>Unrichlist</h1>
                <p>
                    Unrichlist highlights changemakers who are working towards a sustainable, eco-friendly, and socially equitable world.
                </p>
                <p>
                    Unlike traditional "rich lists" that celebrate wealth accumulation, Unrichlist celebrates those who are rich in impact and dedication to creating a better future.
                </p>
            </header>

            <div className="ranking-list">
                {[
                    {
                        rank: 1,
                        name: 'Bill McKibben',
                        image: 'Images/BillMcKibben.png',
                        description: 'Environmentalist and founder of 350.org, a global grassroots movement to combat climate change.',
                        impactAreas: [
                            { label: 'ðŸŒ± Climate Action', color: '#4CAF50' },
                            { label: 'ðŸŒ³ Grassroots Movement', color: '#2196F3' },
                        ],
                    },
                    {
                        rank: 2,
                        name: 'Jane Goodall',
                        image: 'Images/Janegoodall.png',
                        description: 'Primatologist and conservationist known for her work in wildlife conservation and environmental activism.',
                        impactAreas: [
                            { label: 'ðŸ’ Wildlife Conservation', color: '#FF9800' },
                            { label: 'ðŸŒ Environmental Activism', color: '#FFEB3B' },
                        ],
                    },
                    // Add other individuals similarly...
                ].map((person, index) => (
                    <div className="ranking-item" key={index} data-impact="Sustainability">
                        <span className="rank">{person.rank}</span>
                        <div className="vote-buttons">
                            <button className="upvote" aria-label="Upvote">â–²</button>
                            <span className="vote-count">0</span>
                            <button className="downvote" aria-label="Downvote">â–¼</button>
                        </div>
                        <div className="person-info">
                            <img src={person.image} alt={person.name} className="person-image" />
                            <h3 className="person-name">{person.name}</h3>
                            <p>{person.description}</p>
                            <div className="impact-areas">
                                {person.impactAreas.map((impact, i) => (
                                    <span key={i} className="impact-area" style={{ backgroundColor: impact.color }}>{impact.label}</span>
                                ))}
                            </div>
                        </div>
                    </div>
                ))}

                <button className="nominate-btn">Nominate a Changemaker</button>
            </div>

            <footer>
                <p>&copy; 2024 Unrichlist. All rights reserved.</p>
            </footer>
        </div>
    );
};

export default UnrichList;
