import React, { useState, useEffect } from 'react';
import { useUser } from './UserContext';
import './App.css';

const CommentSection = ({ blogId }) => {
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState('');
    const { user } = useUser();

    useEffect(() => {
        // Simulating fetching comments for the specific blog post
        const fetchedComments = [
            { 
                id: 1, 
                text: 'This is a sample comment for blog post ' + blogId, 
                author: 'John Doe',
                date: new Date(2023, 9, 15).toISOString() // Example date
            }
        ];
        setComments(fetchedComments);
    }, [blogId]);

    const handleSubmitComment = (e) => {
        e.preventDefault();
        if (newComment.trim() && user) {
            const comment = {
                id: Date.now(),
                text: newComment,
                author: user.username,
                date: new Date().toISOString()
            };
            setComments(prevComments => [...prevComments, comment]);
            setNewComment('');
        }
    };

    const formatDate = (dateString) => {
        const options = { year: 'numeric', month: 'long', day: 'numeric' };
        return new Date(dateString).toLocaleDateString(undefined, options);
    };

    return (
        <div className="comment-section">
            <h3>Comments</h3>
            {user ? (
                <form onSubmit={handleSubmitComment}>
                    <textarea
                        value={newComment}
                        onChange={(e) => setNewComment(e.target.value)}
                        placeholder="Add a comment..."
                    />
                    <button type="submit">Post Comment</button>
                </form>
            ) : (
                <p>Please log in to post a comment.</p>
            )}
            <div className="comments-list">
                {comments.map(comment => (
                    <div key={comment.id} className="comment">
                        <div className="comment-header">
                            <div className="comment-info">
                                <small className="author">{comment.author}</small>
                                <small className="separator"> â€¢ </small>
                                <small className="date">{formatDate(comment.date)}</small>
                            </div>
                        </div>
                        <p>{comment.text}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default CommentSection;
