import React from 'react';
import { Routes, Route, Link, useParams } from 'react-router-dom';

// Sample blog data
const blogPosts = [
    {
        id: 1,
        title: "The Rise of Vertical Gardens",
        image: ""
    },
    {
        id: 2,
        title: "Solar-Powered Transportation Revolution",
        image: "https://images.unsplash.com/photo-1497435334941-8c899ee9e8e9?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1470&q=80"
    },
    {
        id: 3,
        title: "Eco-Friendly Architecture Innovations",
        image: "https://images.unsplash.com/photo-1518780664697-55e3ad937233?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1465&q=80"
    },
    {
        id: 4,
        title: "Community-Led Renewable Energy Projects",
        image: "https://images.unsplash.com/photo-1509391366360-2e959784a276?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1472&q=80"
    },
];

// Blog list component
const BlogList = () => (
    <div className="blog-list">
        <h1>Solarpunk Blog</h1>
        <ul>
            {blogPosts.map(post => (
                <li key={post.id}>
                    <Link to={`/blog/${post.id}`}>{post.title}</Link>
                </li>
            ))}
        </ul>
    </div>
);

// Individual blog post component
const BlogPost = () => {
    const { id } = useParams();
    const post = blogPosts.find(post => post.id === parseInt(id));

    if (!post) return <div>Post not found</div>;

    return (
        <div className="blog-post">
            <h1>{post.title}</h1>
            <p>{post.content}</p>
            <Link to="/blog">Back to Blog List</Link>
        </div>
    );
};

// Main Blog component
const Blog = () => {
    return (
        <div className="blog-section">
            <Routes>
                <Route path="/" element={<BlogList />} />
                <Route path=":id" element={<BlogPost />} />
            </Routes>
        </div>
    );
};

export default Blog;
