import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import { BlogContext } from './BlogProvider';

const BlogPage = () => {
    const { blogs, isLoading, error } = useContext(BlogContext);
    const [pageNumber, setPageNumber] = useState(1);
    const postsPerPage = 4;

    if (isLoading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;

    const totalPages = Math.ceil(blogs.length / postsPerPage);
    const lastPostIndex = pageNumber * postsPerPage;
    const firstPostIndex = lastPostIndex - postsPerPage;
    const visiblePosts = blogs.slice(firstPostIndex, lastPostIndex);

    const handleNextPage = () => setPageNumber((prev) => Math.min(prev + 1, totalPages));

    const handlePrevPage = () => setPageNumber((prev) => Math.max(prev - 1, 1));

    return (
        <div className="blog-page">
            <h1 className="blog-header">Blog</h1>
            <div className="underline"></div>
            <div className="blog-thumbnails-container">
                <div className="blog-grid">
                    {visiblePosts.map((post) => (
                        <div key={post.id} className="blog-thumbnail">
                            <img src={post.image} alt={post.title} />
                            <h3>{post.title}</h3>
                            <p className="post-info">
                                <span className="author">{post.author}</span>
                                <span className="separator"> - </span>
                                <span className="date">{post.date}</span>
                            </p>
                            <p className="post-excerpt">
                                {post.post.substring(0, 100)}...
                            </p>
                            <div className="button-container">
                                <Link to={`/blog/${post.id}`} className="read-more-button">
                                    Read More
                                </Link>
                            </div>
                        </div>
                    ))}
                </div>
                <div className="pagination">
                    <button onClick={handlePrevPage} disabled={pageNumber === 1}>
                        Prev
                    </button>
                    <span>
                        {pageNumber} of {totalPages}
                    </span>
                    <button onClick={handleNextPage} disabled={pageNumber === totalPages}>
                        Next
                    </button>
                </div>
            </div>
        </div>
    );
};

export default BlogPage;
