import React from 'react';
import { Routes, Route, Link, useParams } from 'react-router-dom';

// Sample blog data
const blogPosts = [
    { id: 1, title: "The Future of Solarpunk", content: "Exploring the optimistic eco-futurist movement..." },
    { id: 2, title: "Sustainable Urban Design", content: "How cities are embracing green technologies..." },
    { id: 3, title: "DIY Solar Projects", content: "Easy solar-powered gadgets you can make at home..." },
];

// Blog list component
const BlogList = () => (
    <div className="blog-list">
        <h1>Solarpunk Blog</h1>
        <ul>
            {blogPosts.map(post => (
                <li key={post.id}>
                    <Link to={`/blog/${post.id}`}>{post.title}</Link>
                </li>
            ))}
        </ul>
    </div>
);

// Individual blog post component
const BlogPost = () => {
    const { id } = useParams();
    const post = blogPosts.find(post => post.id === parseInt(id));

    if (!post) return <div>Post not found</div>;

    return (
        <div className="blog-post">
            <h1>{post.title}</h1>
            <p>{post.content}</p>
            <Link to="/blog">Back to Blog List</Link>
        </div>
    );
};

// Main Blog component
const Blog = () => {
    return (
        <div className="blog-section">
            <Routes>
                <Route path="/" element={<BlogList />} />
                <Route path=":id" element={<BlogPost />} />
            </Routes>
        </div>
    );
};

export default Blog;
