import React from 'react';
import { Link } from 'react-router-dom';
import './App.css';

// Sample data for trending articles and latest posts
const trendingArticles = [
    { id: 1, title: "The Rise of Vertical Gardens", image: "image/beach.jpg" },
    { id: 2, title: "Solar-Powered Transportation Revolution", image: "image/beach.jpg" },
    { id: 3, title: "Eco-Friendly Architecture Innovations", image: "image/beach.jpg" },
    { id: 4, title: "Community-Led Renewable Energy Projects", image: "image/beach.jpg" },
];

const latestPosts = [
    {
        id: 5,
        title: "The Rise of Vertical Gardens",
        image: "image/beach.jpg"
    },
    {
        id: 6,
        title: "Solar-Powered Transportation Revolution",
        image: "image/beach.jpg"
    },
    {
        id: 7,
        title: "Eco-Friendly Architecture Innovations",
        image: "image/beach.jpg"
    },
    {
        id: 8,
        title: "Community-Led Renewable Energy Projects",
        image: "image/beach.jpg"
    },
];








const Home = () => {
    return (
        <div className="home-container">
            <section className="trending-section">
                <h2>Trending</h2>
                <div className="trending-articles">
                    {trendingArticles.map(article => (
                        <div key={article.id} className="trending-article">
                            <img src={article.image} alt={article.title} />
                            <h3>{article.title}</h3>
                            <p className="article-info">
                                <span className="category">{article.category}</span>
                                <span className="date">{article.date}</span>
                            </p>
                            <Link to={`/blog/${article.id}`}>Read More</Link>
                        </div>
                    ))}
                </div>
            </section>
    
            <section className="latest-posts-section">
                <h2>Latest Posts</h2>
                <div className="latest-posts">
                    {latestPosts.map(post => (
                        <div key={post.id} className="latest-post">
                            <img src={post.image} alt={post.title} />
                            <div className="post-content">
                                <h3>{post.title}</h3>
                                <p className="post-info">
                                    <span className="category">{post.category}</span>
                                    <span className="date">Published on: {post.date}</span>
                                </p>
                                <Link to={`/blog/${post.id}`}>Read More</Link>
                            </div>
                        </div>
                    ))}
                </div>
            </section>
        </div>
    );
};
    
    

export default Home;
