import React from 'react';
import { Routes, Route, Link } from 'react-router-dom';
import Home from './Home';
import About from './About';
import Blog from './Blog';
import BlogArticle from './BlogArticle';
import { BlogProvider } from './BlogProvider'; // New import
import './App.css';

function App() {
  return (
    <BlogProvider> {/* Wrap the entire app with BlogProvider */}
      <div className="App">
        <nav className="solarpunk-nav">
          <ul>
            <li><Link to="/">Home</Link></li>
            <li><Link to="/about">About</Link></li>
            <li><Link to="/blog">Blog</Link></li>
          </ul>
        </nav>

        <main className="solarpunk-main">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/about" element={<About />} />
            <Route path="/blog" element={<Blog />} /> {/* Remove blogs prop */}
            <Route path="/blog/:id" element={<BlogArticle />} /> {/* Remove blogs prop */}
          </Routes>
        </main>
      </div>
    </BlogProvider>
  );
}

export default App;
