/*************  âœ¨ Codeium Command ðŸŒŸ  *************/
/**
 * BlogPage component
 *
 * This component renders the blog page, including the blog posts and
 * pagination controls.
 *
 * @returns {React.ReactElement} The blog page component
 */
import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import { BlogContext } from './BlogProvider';

const BlogPage = () => {
    const { blogs, loading, error } = useContext(BlogContext);
    const [currentPage, setCurrentPage] = useState(1);
    const postsPerPage = 4;

    // If the data is loading, display a loading message
    if (loading) return <div>Loading...</div>;

    // If there's an error, display an error message
    if (error) return <div>Error: {error}</div>;

    // Calculate the total number of pages
    const totalPages = Math.ceil(blogs.length / postsPerPage);

    // Calculate the indices of the first and last posts on the current page
    const lastPostIndex = currentPage * postsPerPage;
    const firstPostIndex = lastPostIndex - postsPerPage;

    // Get the current page of posts
    const currentPosts = blogs.slice(firstPostIndex, lastPostIndex);

    /**
     * Handle next page click
     *
     * This function increments the current page number, but only if it's not
     * already on the last page.
     */
    const handleNextPage = () => {
        setCurrentPage((prev) => Math.min(prev + 1, totalPages));
    };

    /**
     * Handle previous page click
     *
     * This function decrements the current page number, but only if it's not
     * already on the first page.
     */
    const handlePrevPage = () => {
        setCurrentPage((prev) => Math.max(prev - 1, 1));
    };

    /**
     * Handle category click
     *
     * This function is a placeholder for handling category clicks.
     * @param {string} category The category to filter by
     */
    const handleCategoryClick = (category) => {
        // Add logic to handle category click
    };

    return (
        <div className="blog-page">
            <h1 className="blog-header">Blog</h1>
            <div className="underline"></div>
            <div className="blog-thumbnails-container">
                <div className="blog-grid">
                    {currentPosts.map((post) => (
                        <div key={post.id} className="blog-thumbnail">
                            <img src={post.image} alt={post.title} />
                            <h3>{post.title}</h3>
                            <p className="post-info">
                                <span className="author">{post.author}</span>
                                <span className="separator"> - </span>
                                <span className="date">{post.date}</span>
                            </p>
                            <p className="post-excerpt">
                                {post.post.substring(0, 100)}...
                            </p>
                            <div className="button-container">
                                <button className="category-button" onClick={() => handleCategoryClick(post.category)}>
                                    {post.category}
                                </button>
                                <Link to={`/blog/${post.id}`} className="read-more-button">
                                    Read More
                                </Link>
                            </div>
                        </div>
                    ))}
                </div>
                <div className="pagination">
                    <button onClick={handlePrevPage} disabled={currentPage === 1}>
                        Prev
                    </button>
                    <span>
                        {currentPage} of {totalPages}
                    </span>
                    <button onClick={handleNextPage} disabled={currentPage === totalPages}>
                        Next
                    </button>
                </div>
            </div>
        </div>
    );
};

export default BlogPage;



