import React, { useState, } from 'react';
import { Link } from 'react-router-dom';
import { FaArrowLeft, FaArrowRight } from 'react-icons/fa';

const BlogThumbnails = () => {
    const blogPosts = [
        {
            id: 1,
            title: "The Rise of Vertical Gardens",
            image: "image/beach.jpg",
            author: "Andrew Hollas",
            post: "Explore how vertical gardens are transforming urban spaces, providing fresh produce and improving air quality in cities.",
            date: "2024-10-05"
        },
        {
            id: 2,
            title: "Solar-Powered Transportation Revolution",
            image: "image/beach.jpg",
            author: "Andrew Hollas",
            post: "Discover the latest innovations in solar-powered vehicles and how they're reshaping the future of transportation.",
            date: "2024-10-07"
        },
        {
            id: 3,
            title: "Eco-Friendly Architecture Innovations",
            image: "image/beach.jpg",
            author: "Andrew Hollas",
            post: "Learn about cutting-edge sustainable architecture practices that are redefining how we design and construct buildings.",
            date: "2024-10-08"
        },
        {
            id: 4,
            title: "Community-Led Renewable Energy Projects",
            image: "image/beach.jpg",
            author: "Andrew Hollas",
            post: "See how local communities are taking charge of their energy future with innovative renewable energy initiatives.",
            date: "2024-10-09"
        },
        {
            id: 5,
            title: "Sustainable Fashion: From Waste to Wardrobe",
            image: "image/beach.jpg",
            author: "Andrew Hollas",
            post: "Discover how innovative designers are transforming recycled materials into high-fashion pieces, revolutionizing the clothing industry.",
            date: "2024-10-11"
        },
        {
            id: 6,
            title: "Urban Farming: The Future of Food Production",
            image: "image/beach.jpg",
            author: "Andrew Hollas",
            post: "Explore how cities are embracing urban farming techniques to create sustainable, local food sources and green spaces.",
            date: "2024-10-13"
        },
        {
            id: 7,
            title: "Biomimicry in Technology: Nature-Inspired Solutions",
            image: "image/beach.jpg",
            author: "Andrew Hollas",
            post: "Learn how scientists and engineers are looking to nature for inspiration, creating groundbreaking technologies that mimic biological processes.",
            date: "2024-10-15"
        }
    
    ];

    const [currentPage, setCurrentPage] = useState(1);
    const postsPerPage = 4;
    const totalPages = Math.ceil(blogPosts.length / postsPerPage);

    const indexOfLastPost = currentPage * postsPerPage;
    const indexOfFirstPost = indexOfLastPost - postsPerPage;
    const currentPosts = blogPosts.slice(indexOfFirstPost, indexOfLastPost);

    const nextPage = () => {
        setCurrentPage(prev => Math.min(prev + 1, totalPages));
    };

    const prevPage = () => {
        setCurrentPage(prev => Math.max(prev - 1, 1));
    };

    return (
        <div className="blog-thumbnails-container">
            <div className="blog-grid">
                {currentPosts.map(post => (
                    <div key={post.id} className="blog-thumbnail">
                        <img src={post.image} alt={post.title} />
                        <h3>{post.title}</h3>
                        <p className="post-info">
                            <span className="author">{post.author}</span>
                            <span className="separator"> - </span>
                            <span className="date">{post.date}</span>
                        </p>
                        <p className="post-excerpt">{post.post.substring(0, 100)}...</p>
                        <Link to={`/blog/${post.id}`}>Read More</Link>
                    </div>
                ))}
            </div>
            <div className="pagination">
                <button onClick={prevPage} disabled={currentPage === 1}>
                    <FaArrowLeft />
                </button>
                <span>{currentPage} of {totalPages}</span>
                <button onClick={nextPage} disabled={currentPage === totalPages}>
                    <FaArrowRight />
                </button>
            </div>
        </div>
    );
};

export default BlogThumbnails;
