import React, { useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import { FaArrowLeft, FaArrowRight } from 'react-icons/fa';
import { BlogContext } from './BlogProvider';

const BlogThumbnails = () => {
    const { blogs, loading, error } = useContext(BlogContext);
    const [currentPage, setCurrentPage] = useState(1);
    const postsPerPage = 6; // Increased to 6 for two columns

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;

    const totalPages = Math.ceil(blogs.length / postsPerPage);

    const indexOfLastPost = currentPage * postsPerPage;
    const indexOfFirstPost = indexOfLastPost - postsPerPage;
    const currentPosts = blogs.slice(indexOfFirstPost, indexOfLastPost);

    const nextPage = () => {
        setCurrentPage(prev => Math.min(prev + 1, totalPages));
    };

    const prevPage = () => {
        setCurrentPage(prev => Math.max(prev - 1, 1));
    };

    return (
        <div className="blog-page">
            <div className="blog-thumbnails-container">
                <div className="blog-grid">
                    {currentPosts.map(post => (
                        <div key={post.id} className="blog-thumbnail">
                            <img src={post.image} alt={post.title} />
                            <h3>{post.title}</h3>
                            <p className="post-info">
                                <span className="author">{post.author}</span>
                                <span className="separator"> - </span>
                                <span className="date">{post.date}</span>
                            </p>
                            <p className="post-excerpt">{post.post.substring(0, 100)}...</p>
                            <Link to={`/blog/${post.id}`}>Read More</Link>
                        </div>
                    ))}
                </div>
                <div className="pagination">
                    <button onClick={prevPage} disabled={currentPage === 1}>
                        <FaArrowLeft />
                    </button>
                    <span>{currentPage} of {totalPages}</span>
                    <button onClick={nextPage} disabled={currentPage === totalPages}>
                        <FaArrowRight />
                    </button>
                </div>
            </div>
            <div className="sidebar">
                <h2>Recent Posts</h2>
                <ul>
                    {blogs.slice(0, 5).map(post => (
                        <li key={post.id}>
                            <Link to={`/blog/${post.id}`}>{post.title}</Link>
                        </li>
                    ))}
                </ul>
                {/* Add more sidebar content as needed */}
            </div>
        </div>
    );
};

export default BlogThumbnails;
