import React, { useState, useEffect, useContext } from 'react';
import { CommentSection as ReactCommentSection } from 'react-comments-section';
import { BlogContext } from './BlogProvider';
import 'react-comments-section/dist/index.css'; // Import the CSS
import './CommentSection.css'; // Create this file for custom styles

const CommentSection = ({ blogId }) => {
    const [comments, setComments] = useState([]);
    const { blogs } = useContext(BlogContext);

    useEffect(() => {
        // Simulating fetching comments for the specific blog post
        const fetchedComments = [
            {
                userId: 'user-1',
                comId: 'comment-1',
                fullName: 'John Doe',
                userProfile: 'https://example.com/user-profile',
                text: 'This is a sample comment for blog post ' + blogId,
                avatarUrl: 'https://ui-avatars.com/api/?name=John+Doe',
                replies: []
            }
        ];
        setComments(fetchedComments);
    }, [blogId]);

    const handleSubmitComment = (data) => {
        console.log('New comment:', data);
        const newComment = {
            ...data,
            userId: 'current-user',
            comId: `comment-${Date.now()}`, // Use timestamp for unique ID
            userProfile: 'https://example.com/current-user-profile',
            avatarUrl: 'https://ui-avatars.com/api/?name=Current+User',
            replies: []
        };
        setComments([...comments, newComment]);
    };

    const handleUpdateComments = (data) => {
        console.log('Updated comments:', data);
        setComments(data);
    };

    // Find the current blog post
    const currentBlog = blogs.find(blog => blog.id === parseInt(blogId));

    if (!currentBlog) {
        return <div className="error-message">Blog post not found</div>;
    }

    return (
        <div className="comment-section-wrapper">
            <h3>Comments</h3>
            <ReactCommentSection
                currentUser={{
                    currentUserId: 'current-user',
                    currentUserImg: 'https://ui-avatars.com/api/?name=Current+User',
                    currentUserProfile: 'https://example.com/current-user-profile',
                    currentUserFullName: 'Current User'
                }}
                commentData={comments}
                onSubmitAction={handleSubmitComment}
                currentData={handleUpdateComments}
                logIn={{
                    loginLink: 'https://example.com/login',
                    signupLink: 'https://example.com/signup'
                }}
            />
        </div>
    );
};

export default CommentSection;
