import React from 'react';
import { Link } from 'react-router-dom';
import './App.css';

// Sample data for trending articles and latest posts
const trendingArticles = [
    { id: 1, title: "The Rise of Vertical Gardens", image: "" },
    { id: 2, title: "Solar-Powered Transportation Revolution", image: "images/beach.jpg" },
    { id: 3, title: "Eco-Friendly Architecture Innovations", image: "https://example.com/eco-architecture.jpg" },
    { id: 4, title: "Community-Led Renewable Energy Projects", image: "https://example.com/community-energy.jpg" },
];

const latestPosts = [
    { id: 5, title: "DIY Solar Panel Installation Guide", date: "2024-10-05" },
    { id: 6, title: "The Future of Urban Farming", date: "2024-10-03" },
    { id: 7, title: "Sustainable Fashion: Trends and Innovations", date: "2024-10-01" },
    { id: 8, title: "Green Technology in Everyday Life", date: "2024-09-28" },
    { id: 9, title: "Reducing Carbon Footprint: Simple Steps", date: "2024-09-25" },
];

const Home = () => {
    return (
        <div className="home-container">
            <section className="trending-section">
                <h2>Trending Articles</h2>
                <div className="trending-articles">
                    {trendingArticles.map(article => (
                        <div key={article.id} className="trending-article">
                            <img src={article.image} alt={article.title} />
                            <h3>{article.title}</h3>
                            <Link to={`/blog/${article.id}`}>Read More</Link>
                        </div>
                    ))}
                </div>
            </section>

            <section className="latest-posts-section">
                <h2>Latest Posts</h2>
                <div className="latest-posts">
                    {latestPosts.map(post => (
                        <div key={post.id} className="latest-post">
                            <h3>{post.title}</h3>
                            <p>Published on: {post.date}</p>
                            <Link to={`/blog/${post.id}`}>Read More</Link>
                        </div>
                    ))}
                </div>
            </section>
        </div>
    );
};

export default Home;
